import React,{Component} from 'react';
import {GalleryWrapper} from './style'
import Carousel from 'nuka-carousel';
import staticImage from '../../static/images/no_image.png'

export class ImageGallery extends Component {
  state = {
    imageExist: true
  }

 render () {
  const galleryPhotos = [...this.props.photos.slice(0,5)]
  const gallery = this.props.photos.length !== 0 ?
   galleryPhotos.map(image => this.state.imageExist ? 
    <img 
     src={`${this.props.imageURL}${image}.jpg`} 
     alt="original" 
     key={this.props.id}
     height="230"
     onError={() => this.setState({imageExist: false})}
    /> : null
   ) : 
    <img src={`${staticImage}`} 
     alt="default" 
     key={this.props.id} 
     height="230"/> 

     console.log('GALLERY: ', gallery)

  return (
   <GalleryWrapper> 
     <Carousel>
      {this.state.imageExist ? gallery : 
       <img src={`${staticImage}`} 
        alt="default" 
        key={this.props.id} 
        height="230"/> }
     </Carousel>
    </GalleryWrapper>
  )
 }
}

export default ImageGallery
