import React from 'react';
import {PlaceWrapper, PlaceDetails} from './style'
import ImageGallery from '../ImageGallery'
import {STATIC_URL_IMAGES} from '../../consts'
import StarRatings from 'react-star-ratings';

const RentalPlace = (props) => {
 const imageURL=`${STATIC_URL_IMAGES}${props.photoDirectory}/${props.id}/t/`
 const reducePlaceDescLength = content => {
  return content.length > 40
    ? `${content.substr(0, 27)}...`
    : content
  }

 return (
  <PlaceWrapper>
   <ImageGallery 
    photos={props.photos}
    imageURL={imageURL} 
    id={props.id}/>
    <PlaceDetails>
     <h3>{reducePlaceDescLength(props.title)}</h3>
     <p>{props.teaser}</p>
     <p>Rating: {props.rating}</p>
     <StarRatings
        rating={80}
        starRatedColor="gold"
        starDimension="20px"
        starSpacing="2px"
      />
     <p>Best Price on: {props.provider}</p>
    </PlaceDetails>
  </PlaceWrapper>
 )
}

export default RentalPlace
