import React from 'react';
import {PlaceWrapper, PlaceDetails} from './style'
import ImageGallery from '../ImageGallery'
import {STATIC_URL_IMAGES} from '../../consts'
import StarRatings from 'react-star-ratings';

const RentalPlace = (props) => {
 const imageURL=`${STATIC_URL_IMAGES}${props.photoDirectory}/${props.id}/t/`
 //Reduce title length after 40 characters adding three dots
 const reducePlaceTiteLength = content => {
  return content.length > 40
    ? `${content.substr(0, 27)}...`
    : content
  }
  //Calculate place rating based on precentage 
  const ratingValue = (props.rating / 100 * 50) /10
  const rating = props.rating !== 0 ? 
   <StarRatings
     rating={ratingValue}
     starRatedColor="gold"
     starDimension="20px"
     starSpacing="2px"
   /> : null

 return (
  <PlaceWrapper>
   <ImageGallery 
    photos={props.photos}
    imageURL={imageURL} 
    id={props.id}/>
    <PlaceDetails>
     <h3>{reducePlaceTiteLength(props.title)}</h3>
     <p>{props.teaser}</p>
     <p>Rating: {rating.toFixed(1)}</p>
     {rating}
     <p>Best Price on: {props.provider}</p>
    </PlaceDetails>
  </PlaceWrapper>
 )
}

export default RentalPlace
